r_t(\boldsymbol \theta)&amp;amp;=\frac{\pi_{\theta_{old}}(a_t \mid s_t)}{\pi_\theta(a_t \mid s_t)}, \\

\operatorname{clip}(x,B,C) \ (B&amp;lt;C) &amp;amp;=\left \{
    \begin{aligned}
    B, &amp;amp; &amp;amp; x &amp;lt; B \\
    x, &amp;amp; &amp;amp; B &amp;lt; x &amp;lt; C \\
    C, &amp;amp; &amp;amp; x &amp;gt; C
    \end {aligned} \right. \\

KL(p(x), q(x)) &amp;amp; = \left \{ \begin{aligned}
    \int_x p(x)\ln{\frac{p(x)}{q(x)}}\, dx &amp;amp; &amp;amp; \text{for continuous x} \\
    \sum_x p(x)\ln{\frac{p(x)}{q(x)}} &amp;amp; &amp;amp; \text{for discrete x} \\
\end{aligned} \right .

\end{aligned}
